
public class As2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i,j;

		    for(i=1;i<=4;i++) {

		        for (j=1;j<=4-i;j++) {

		            System.out.print("");

		        }

		        for(j=1;j<=i;j++) {

		            System.out.print(j);

		        }

		        for(j=i-1;j>=1;j--) {

		            System.out.print(j);

		        }

		        System.out.println();

		    }

		    for(i=3;i>=1;i--) {

		        for(j=1;j<=4-i;j++) {

		            System.out.print("");

		        }

		        

		       
		        System.out.println();

		    }

		   
		}
		
	}


